import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { Box, Typography } from '@mui/material';
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';

import RandomRecipe from './RandomRecipe';

import RightArrowIcon from '../assets/icons/right-arrow.png';
import LeftArrowIcon from '../assets/icons/left-arrow.png';
import { ImprovedImageContext } from '../utils/Contexts';

const LeftArrow = () => {
  const { isFirstItemVisible, scrollPrev } = useContext(VisibilityContext);

  return (
    <Typography disabled={isFirstItemVisible} onClick={() => scrollPrev()} className="right-arrow">
      <img src={LeftArrowIcon} alt="right-arrow" />
    </Typography>
  );
};

const RightArrow = () => {
  const { isLastItemVisible, scrollNext } = useContext(VisibilityContext);

  return (
    <Typography disabled={isLastItemVisible} onClick={() => scrollNext()} className="left-arrow">
      <img src={RightArrowIcon} alt="right-arrow" />
    </Typography>
  );
};

const HorizontalScrollBar = ({ data }) => {

  const { improveImages, setImproveImages } = useContext(ImprovedImageContext);
  
  if (data) {
    return (
      <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}>
        {data.map((item) => (
          <Link key={item.id} to={`/recipe/${item.id}`}>
            <Box
              itemID={item.id}
              title={item.title}
              m='0 40px'
            >
              <RandomRecipe image={item.image}  />
              {/* {item.title}
              <img src={item.image} /> */}
            </Box>
          </Link>
        )
        )}
      </ScrollMenu>
    )
  }
}

export default HorizontalScrollBar
